mapper.select:
		SELECT * 
		  FROM products a
        WHERE a.menu_name = #{value}

	SELECT count(1) as total_count
	      FROM chain_store a
INNER JOIN products b
        ON a.chain_no = b.chain_no
       AND b.display_yn = 'Y'
	
	   AND a.chain_name = #{brandList}
	
	
	   AND b.category = #{menuSelect}
	
	
	   AND b.single_price BETWEEN #{priceSt} AND #{priceEnd}
	
	
	   AND b.calory BETWEEN #{calSt} AND #{calEnd}

SELECT a.chain_name , b.menu_name , b.single_price , b.set_price , b.calory , b.img_url FROM chain_store a INNER JOIN products b ON a.chain_no = b.chain_no AND b.display_yn = 'Y'
AND a.chain_name = #{brandList}
AND b.category = #{menuSelect}
AND b.single_price BETWEEN #{priceSt} AND #{priceEnd}
AND b.calory BETWEEN #{calSt} AND #{calEnd}
LIMIT #{startPage}, 6

	SELECT a.admin_no
			  , a.name
			  , a.use_active
      FROM admin_user a
    WHERE a.id = #{idVal}
		 AND a.pass = #{passVal}

mapper.update:
UPDATE products
      SET chain_no = #{chain_no}
	      , menu_name = #{menu_name}
	      , single_price = #{single_price}
	      , set_price = #{set_price}
	      , calory = #{calory}
	      , img_url = #{img_url}
WHERE product_no = #{product_no}

mapper.insert:
INSERT INTO products(chain_no, menu_name, single_price, set_price, calory, display_yn, img_url, category)
VALUES(#{chain_no}
		  , #{menu_name}
		  , #{single_price}
		  , #{set_price}
		  , #{calory}
		  , #{display_yn}
		  , #{img_url}
		  , #{category})

mapper.update:
UPDATE display
      SET display_type = #{display_type}
	      , name = #{name}
	      , display_yn = #{display_yn}
	      , priority = #{priority}
	      , img_url = #{img_url}
WHERE display_no = #{display_no}

mapper.insert:
INSERT INTO display(display_type, name, img_url, display_yn, priority)
VALUES(#{display_type}
		  , #{name}
		  , #{img_url}
		  , #{display_yn}
		  , #{priority})
